import React from "react";
import Card from "./Card";
// By extending the React.Component class, Counter inherits functionality from it
class Score extends React.Component {
  // Setting the initial state of the Counter component
  state = {
    score: 0,
    topScore:0
  };

  // handleIncrement increments this.state.count by 1
  handleIncrement = () => {
    // We always use the setState method to update a component's state
    this.setState({ score: this.state.score + 1 });
  };

  // The render method returns the JSX that should be rendered
  render() {
    return (
      <div className="card text-center">
        <div className="card-header bg-primary text-white">
          <p className="card-text">Score: {this.state.score}</p>
          <p className="card-text">Top Score: {this.state.topScore}</p>
        </div>
        <div className="card-body">
          < Card image="https://www.google.com/url?sa=i&source=images&cd=&cad=rja&uact=8&ved=2ahUKEwi9_6v-44XhAhVTgp4KHSVKDUQQjRx6BAgBEAU&url=https%3A%2F%2Fwww.youtube.com%2Fwatch%3Fv%3DSe78x_Y5sZk&psig=AOvVaw1vHolS-LCxubw64Hms98Fr&ust=1552795448026486" />
          <button className="btn btn-primary" onClick={this.handleIncrement}>
            Increment
          </button>
        </div>
      </div>
    );
  }
}

export default Score;
